$(document).ready(function(){
  $('.rating_star').click(function(){
    var user_id = $(this).attr('data-user-id');
    var stars = $(this).attr('data-stars');

    for(i = 1; i <= 5; i++) {
      if( i <= stars){
        $('#' + user_id + '_' +i).removeClass('glyphicon-star-empty');
        $('#' + user_id + '_' +i).addClass('glyphicon-star');
      } else {
        $('#' + user_id + '_' +i).removeClass('glyphicon-star');
        $('#' + user_id + '_' +i).addClass('glyphicon-star-empty');
      }
    }

    $.ajax({
      type: 'post',
      url: '/users/' + user_id + '/rate',
      data: {rating: stars}
    }); 
  })
})
